for EFIX_PKG in "efix1.tar|pakiet1.rte" "efix2.tar|pakiet2.rte" "efix3.tar|pakiet3.rte"; do EFIX=${EFIX_PKG%%|*}; PKG=${EFIX_PKG##*|}; lslpp -L | grep -iq "${PKG%.rte}" && echo "$PKG już zainstalowany" && continue; echo "Pobieram $EFIX..."; perl -MHTTP::Tiny -e 'HTTP::Tiny->new->mirror("'"link/security/$EFIX"'","/tmp/$EFIX")'; echo "Rozpakowuję $EFIX..."; tar -xvf "/tmp/$EFIX" -C /tmp; cd "/tmp/${EFIX%.tar}"; echo "Instaluję $PKG..."; installp -acXYd . "$PKG"; echo "EMGR $PKG..."; perl -e '$pkg_upd="'"$PKG"'"; $_=qx{/usr/sbin/emgr -P}; /(^$pkg_upd\s+\S+\s+(\S+))/m and exec "/usr/sbin/emgr","-r","-L",$2'; cd /tmp; done
